import React, { useEffect, useState } from 'react'

const Theme = () => {
  const [light, setLight] = useState(true);

  return (
    <div className={`flex ${light?'flex-row':'flex-row-reverse'} w-10 bg-gray-200 rounded-2xl p-1`}>
        <button 
        className='flex justify-center items-center bg-white p-1 w-[50%] rounded-2xl shadow-md'
        onClick={()=>{setLight(!light)}}
        >
            <span class="material-symbols-outlined">
                {light ? 'light_mode':'dark_mode'}
            </span>
        </button>
    </div>
  )
}

export default Theme
