import React, { useState } from 'react'

const Theme = () => {
    const [light, isLight] = useState(true)
  return (
    <div className='flex w-20 bg-white rounded-2xl p-1'>
        <button 
        className='flex justify-center items-center bg-gray-200 p-1 w-[50%] rounded-2xl shadow-md'
        onClick={isLight(!light)}
        >
            <span class="material-symbols-outlined">
                {light ? 'light_mode':'dark_mode'}
            </span>
        </button>
    </div>
  )
}

export default Theme
